function fileAPIAvailable(){return window.File&&window.FileReader&&window.FileList}function previewImage(b){if(!fileAPIAvailable()){$("#preview").html("Your browser does not support image previews");return}var d=b.target.files;for(var c=0,e;e=d[c];++c){if(!e.type.match("image.*")){$("#preview").html("Selected file is not an image");return}var a=new FileReader();a.onload=(function(f){return function(h){var g=$('<img class="thumb" src="'+h.target.result+'" title="'+f.name+'"/>');$("#preview").html(g)}})(e);a.readAsDataURL(e)}}function globalInit(a,b){$("#"+a+"_"+b).change(function(c){$("#preview").html('<img src="images/loading.gif"/>');previewImage(c)})}function datePicker(a){$("#"+a).datepicker()};