function translate(a){$.ajax({type:"POST",contentType:"application/json; charset=utf-8",url:"/index.php/api/language",data:JSON.stringify(a),dataType:"json",success:function(b){$("#page,#flags").fadeOut("slow",function(){$.cookie("loadEffect","true");location.reload()})}})}$(document).ready(function(){if($.cookie("loadEffect")=="true"){$("#page,#flags").hide();$("#page,#flags").fadeIn("slow");$.cookie("loadEffect",null)}$("#"+$("#language").html()+"_flag").addClass("current")});