function translate(a){$.ajax({type:"POST",contentType:"application/json; charset=utf-8",url:"/index.php/api/language",data:JSON.stringify(a),dataType:"json",success:function(b){$("#page").fadeOut("slow",function(){$.cookie("loadEffect","true");location.reload()})}})}$(document).ready(function(){if($.cookie("loadEffect")=="true"){$("#page").hide();$("#page").fadeIn("slow");$.cookie("loadEffect",null)}});